mapper.select:
SELECT eo.*, u.nickname AS user_nickname FROM exchange_order eo LEFT JOIN user_info u ON eo.user_id = u.id
AND eo.user_id = #{userId} AND eo.status = #{status}
AND (eo.order_no LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%') OR eo.receiver_name LIKE CONCAT('%', #{keyword}, '%') OR eo.receiver_phone LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY eo.create_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM exchange_order eo LEFT JOIN user_info u ON eo.user_id = u.id
AND eo.user_id = #{userId} AND eo.status = #{status}
AND (eo.order_no LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%') OR eo.receiver_name LIKE CONCAT('%', #{keyword}, '%') OR eo.receiver_phone LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE exchange_order
status = #{status}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, receiver_address = #{receiverAddress}, logistics_info = #{logisticsInfo}, remark = #{remark}, update_time = #{updateTime}
WHERE id = #{id}
