mapper.select:
SELECT * FROM record_info WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT * FROM record_info WHERE task_id = #{taskId}
ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT * FROM record_info

    AND audit_status = #{status}

ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM record_info

    AND user_id = #{userId}
    AND task_id = #{taskId}
    AND audit_status = #{status}
