mapper.update:
UPDATE user_info
nickname = #{nickname}, avatar = #{avatar}, email = #{email}, phone = #{phone}, status = #{status}, update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT * FROM user_info


        (username LIKE CONCAT('%', #{keyword}, '%')
         OR nickname LIKE CONCAT('%', #{keyword}, '%')
         OR email LIKE CONCAT('%', #{keyword}, '%')
         OR phone LIKE CONCAT('%', #{keyword}, '%'))


        AND role = #{role}


        AND status = #{status}


ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM user_info
(username LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))
AND role = #{role}
AND status = #{status}
